import React, { useState } from 'react';
import { Puzzle, caesarDecode } from '../data/puzzles';

interface PuzzleOverlayProps {
    puzzle: Puzzle;
    onSolve: () => void;
    onClose: () => void;
}

export default function PuzzleOverlay({ puzzle, onSolve, onClose }: PuzzleOverlayProps) {
    const [answer, setAnswer] = useState('');
    const [error, setError] = useState('');
    const [caesarShift, setCaesarShift] = useState(0);
    const [decodedMessage, setDecodedMessage] = useState(puzzle.type === 'caesar' ? puzzle.content.encodedMessage : '');
    const [fixedBugs, setFixedBugs] = useState<number[]>([]);
    const [selectedLine, setSelectedLine] = useState<number | null>(null);

    const handleSubmit = () => {
        const userAnswer = answer.toUpperCase().trim();
        const correctAnswer = typeof puzzle.solution === 'string' ? puzzle.solution.toUpperCase() : '';

        if (userAnswer === correctAnswer) {
            onSolve();
        } else {
            setError('Incorrect answer. Try again!');
            setTimeout(() => setError(''), 2000);
        }
    };

    const handleCaesarShift = () => {
        const decoded = caesarDecode(puzzle.content.encodedMessage, caesarShift);
        setDecodedMessage(decoded);

        if (caesarShift === 12 && decoded === 'PROCEED') {
            setTimeout(() => onSolve(), 500);
        }
    };

    const handleBugFix = (bugId: number, selectedFix: string, correctFix: string) => {
        if (selectedFix === correctFix) {
            const newFixedBugs = [...fixedBugs, bugId];
            setFixedBugs(newFixedBugs);
            setSelectedLine(null);

            // Check if all bugs are fixed
            if (newFixedBugs.length === 5) {
                setTimeout(() => onSolve(), 500);
            }
        } else {
            setError('Wrong fix! Try again.');
            setTimeout(() => setError(''), 2000);
        }
    };

    const overlayStyle: React.CSSProperties = {
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        background: 'rgba(0, 0, 0, 0.9)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: 1000,
        fontFamily: "'Consolas', 'Monaco', monospace",
    };

    const modalStyle: React.CSSProperties = {
        background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 100%)',
        border: '2px solid #00ff88',
        borderRadius: '15px',
        padding: '30px',
        maxWidth: '800px',
        width: '90%',
        maxHeight: '90vh',
        overflow: 'auto',
        boxShadow: '0 0 50px rgba(0, 255, 136, 0.3)',
    };

    const titleStyle: React.CSSProperties = {
        color: '#00ff88',
        fontSize: '28px',
        fontWeight: 'bold',
        marginBottom: '20px',
        textAlign: 'center',
        textShadow: '0 0 10px #00ff88',
    };

    const descStyle: React.CSSProperties = {
        color: '#ffffff',
        fontSize: '16px',
        marginBottom: '20px',
        lineHeight: 1.6,
    };

    const codeBlockStyle: React.CSSProperties = {
        background: '#0d1117',
        border: '1px solid #30363d',
        borderRadius: '8px',
        padding: '15px',
        fontFamily: "'Consolas', monospace",
        fontSize: '14px',
        color: '#c9d1d9',
        whiteSpace: 'pre-wrap',
        marginBottom: '20px',
        overflowX: 'auto',
    };

    const inputStyle: React.CSSProperties = {
        width: '100%',
        padding: '12px',
        fontSize: '18px',
        background: '#0d1117',
        border: '2px solid #30363d',
        borderRadius: '8px',
        color: '#ffffff',
        marginBottom: '15px',
    };

    const buttonStyle: React.CSSProperties = {
        padding: '12px 30px',
        fontSize: '16px',
        background: 'linear-gradient(135deg, #00ff88 0%, #00cc6a 100%)',
        color: '#000',
        border: 'none',
        borderRadius: '8px',
        cursor: 'pointer',
        fontWeight: 'bold',
        marginRight: '10px',
    };

    const hintStyle: React.CSSProperties = {
        color: '#888',
        fontSize: '14px',
        fontStyle: 'italic',
        marginTop: '15px',
    };

    const renderPuzzleContent = () => {
        switch (puzzle.type) {
            case 'hex':
                return (
                    <>
                        <div style={codeBlockStyle}>
                            {puzzle.content.hex}
                        </div>
                        <p style={hintStyle}>Hint: {puzzle.content.hint}</p>
                        <input
                            type="text"
                            value={answer}
                            onChange={(e) => setAnswer(e.target.value)}
                            placeholder="Enter the decoded password..."
                            style={inputStyle}
                            onKeyDown={(e) => e.key === 'Enter' && handleSubmit()}
                        />
                    </>
                );

            case 'pseudocode':
                return (
                    <>
                        <div style={codeBlockStyle}>
                            {puzzle.content.code}
                        </div>
                        <p style={hintStyle}>Hint: {puzzle.content.hint}</p>
                        <input
                            type="text"
                            value={answer}
                            onChange={(e) => setAnswer(e.target.value)}
                            placeholder="Enter the return value..."
                            style={inputStyle}
                            onKeyDown={(e) => e.key === 'Enter' && handleSubmit()}
                        />
                    </>
                );

            case 'algorithm':
                return (
                    <>
                        <div style={codeBlockStyle}>
                            {puzzle.content.algorithm}
                        </div>
                        <p style={hintStyle}>Hint: {puzzle.content.hint}</p>
                        <input
                            type="text"
                            value={answer}
                            onChange={(e) => setAnswer(e.target.value)}
                            placeholder="Enter the decoded word..."
                            style={inputStyle}
                            onKeyDown={(e) => e.key === 'Enter' && handleSubmit()}
                        />
                    </>
                );

            case 'caesar':
                return (
                    <>
                        <div style={{ marginBottom: '20px' }}>
                            <p style={{ color: '#fff', marginBottom: '10px' }}>Encoded Message:</p>
                            <div style={{ ...codeBlockStyle, fontSize: '24px', textAlign: 'center', letterSpacing: '5px' }}>
                                {puzzle.content.encodedMessage}
                            </div>
                        </div>
                        <div style={{ marginBottom: '20px' }}>
                            <p style={{ color: '#fff', marginBottom: '10px' }}>Decoded Message (Shift: {caesarShift}):</p>
                            <div style={{
                                ...codeBlockStyle,
                                fontSize: '24px',
                                textAlign: 'center',
                                letterSpacing: '5px',
                                color: caesarShift === 12 ? '#00ff88' : '#c9d1d9',
                                textShadow: caesarShift === 12 ? '0 0 10px #00ff88' : 'none'
                            }}>
                                {decodedMessage}
                            </div>
                        </div>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '20px' }}>
                            <label style={{ color: '#fff' }}>Shift Value:</label>
                            <input
                                type="number"
                                value={caesarShift}
                                onChange={(e) => setCaesarShift(parseInt(e.target.value) || 0)}
                                min={0}
                                max={25}
                                style={{ ...inputStyle, width: '80px', marginBottom: 0 }}
                            />
                            <button onClick={handleCaesarShift} style={buttonStyle}>
                                SHIFT
                            </button>
                        </div>
                        <p style={hintStyle}>Hint: {puzzle.content.hint}</p>
                    </>
                );

            case 'debug':
                return (
                    <>
                        <p style={{ color: '#00ff88', marginBottom: '10px' }}>
                            Bugs Fixed: {fixedBugs.length} / 5
                        </p>
                        <div style={{ ...codeBlockStyle, padding: 0 }}>
                            {puzzle.content.code.map((line: any) => (
                                <div
                                    key={line.line}
                                    onClick={() => line.bug && !fixedBugs.includes(line.bugId) && setSelectedLine(line.line)}
                                    style={{
                                        padding: '4px 15px',
                                        cursor: line.bug && !fixedBugs.includes(line.bugId) ? 'pointer' : 'default',
                                        background: fixedBugs.includes(line.bugId)
                                            ? 'rgba(0, 255, 136, 0.2)'
                                            : selectedLine === line.line
                                                ? 'rgba(255, 200, 0, 0.3)'
                                                : 'transparent',
                                        borderLeft: fixedBugs.includes(line.bugId)
                                            ? '3px solid #00ff88'
                                            : line.bug
                                                ? '3px solid #ff4444'
                                                : '3px solid transparent',
                                        display: 'flex',
                                        alignItems: 'center',
                                    }}
                                >
                                    <span style={{
                                        color: '#666',
                                        marginRight: '15px',
                                        minWidth: '25px',
                                        userSelect: 'none'
                                    }}>
                                        {line.line}
                                    </span>
                                    <span style={{
                                        textDecoration: fixedBugs.includes(line.bugId) ? 'line-through' : 'none',
                                        color: fixedBugs.includes(line.bugId) ? '#666' : '#c9d1d9',
                                    }}>
                                        {line.text || ' '}
                                    </span>
                                </div>
                            ))}
                        </div>

                        {selectedLine !== null && (
                            <div style={{
                                marginTop: '20px',
                                padding: '15px',
                                background: 'rgba(255, 200, 0, 0.1)',
                                border: '1px solid #ffc800',
                                borderRadius: '8px'
                            }}>
                                <p style={{ color: '#ffc800', marginBottom: '10px' }}>
                                    Select the correct fix for line {selectedLine}:
                                </p>
                                {puzzle.content.code
                                    .find((l: any) => l.line === selectedLine)
                                    ?.options?.map((option: string, idx: number) => (
                                        <button
                                            key={idx}
                                            onClick={() => {
                                                const line = puzzle.content.code.find((l: any) => l.line === selectedLine);
                                                handleBugFix(line.bugId, option, line.fix);
                                            }}
                                            style={{
                                                display: 'block',
                                                width: '100%',
                                                padding: '10px',
                                                marginBottom: '8px',
                                                background: '#0d1117',
                                                border: '1px solid #30363d',
                                                borderRadius: '5px',
                                                color: '#c9d1d9',
                                                cursor: 'pointer',
                                                textAlign: 'left',
                                                fontFamily: "'Consolas', monospace",
                                            }}
                                        >
                                            {option}
                                        </button>
                                    ))}
                            </div>
                        )}
                        <p style={hintStyle}>Hint: {puzzle.content.hint}</p>
                    </>
                );

            default:
                return null;
        }
    };

    return (
        <div style={overlayStyle} onClick={onClose}>
            <div style={modalStyle} onClick={(e) => e.stopPropagation()}>
                <h2 style={titleStyle}>üîí PUZZLE {puzzle.id}: {puzzle.title}</h2>
                <p style={descStyle}>{puzzle.description}</p>

                {renderPuzzleContent()}

                {error && (
                    <p style={{ color: '#ff4444', textAlign: 'center', marginTop: '15px' }}>
                        ‚ùå {error}
                    </p>
                )}

                <div style={{ marginTop: '20px', display: 'flex', justifyContent: 'center' }}>
                    {puzzle.type !== 'caesar' && puzzle.type !== 'debug' && (
                        <button onClick={handleSubmit} style={buttonStyle}>
                            SUBMIT
                        </button>
                    )}
                    <button
                        onClick={onClose}
                        style={{ ...buttonStyle, background: '#444', color: '#fff' }}
                    >
                        CLOSE (ESC)
                    </button>
                </div>
            </div>
        </div>
    );
}
