    id: number;
    type: 'hex' | 'pseudocode' | 'algorithm' | 'caesar' | 'debug';
    position: { x: number; z: number };
    title: string;
    description: string;
    content: any;
    solution: string | number[];
}

// Puzzle 1: Hex Decode
