    content: {
        code: `FUNCTION mystery(n)
    IF n <= 0 THEN
        RETURN 0
    END IF
    
    SET result = 0
    FOR i FROM 1 TO n DO
        result = result + i
    END FOR
    
    RETURN result - n - 3
END FUNCTION

What is mystery(10)?`,
        hint: 'Sum of 1 to 10 is 55. Then subtract n and 3.'
    },
    solution: '42'
};

// Puzzle 3: Algorithm Challenge (no specific language)
const puzzle3: Puzzle = {
    id: 3,
    type: 'algorithm',
    position: { x: 15, z: 20 },
