// Puzzle 5: C Code Debugger (25 lines, 5 bugs)
const puzzle5: Puzzle = {
    id: 5,
    type: 'debug',
    position: { x: 35, z: 35 },
    title: 'DEBUG THE CODE',
    description: 'This C program has 5 bugs. Click on each buggy line and select the fix:',
