            return char;
        })
        .join('');
}

// Check if Caesar decode is correct
export function checkCaesarSolution(encodedMessage: string, shift: number): boolean {
    const decoded = caesarDecode(encodedMessage, shift);
    return decoded === 'PROCEED';
}
